import React, { useState, useEffect } from 'react';
import { NavLink } from 'react-router-dom';
import { CContainer, CDropdown, CDropdownToggle, CDropdownMenu, CDropdownItem } from '@coreui/react';
import CIcon from '@coreui/icons-react';
import { cilList } from '@coreui/icons';
import logo from '../assets/bee/bee1.png';

const AppHeader = () => {
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const [isMobileScreen, setIsMobileScreen] = useState(false);

  useEffect(() => {
    const handleResize = () => {
      setIsMobileScreen(window.innerWidth <= 768); // Adjust this threshold according to your needs
    };

    // Initial check
    handleResize();

    // Event listener for window resize
    window.addEventListener('resize', handleResize);

    // Cleanup on unmount
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  const toggleDropdown = () => {
    setDropdownOpen(!dropdownOpen);
  };

  return (
    <div className="side-menu__block">
      <a href="#" className="side-menu__toggler side-menu__close-btn" onClick={toggleDropdown}>
      </a>
      <div className="side-menu__block-inner">
        <a href="index.html" className="side-menu__logo"><img src={logo} alt="" width="190" /></a>
        {isMobileScreen && (
          <CDropdown isOpen={dropdownOpen} toggle={toggleDropdown}>
            <CDropdownToggle caret>
              <CIcon icon={cilList} />
            </CDropdownToggle>
            <CDropdownMenu>
              <CDropdownItem>
                <NavLink to="/tweetstats_worldwide">TweetStats Worldwide</NavLink>
              </CDropdownItem>
              <CDropdownItem>
                <NavLink to="/perspectives_updates">Perspectives and Updates</NavLink>
              </CDropdownItem>
              {/* Add more dropdown items as needed */}
            </CDropdownMenu>
          </CDropdown>
        )}
      </div>
    </div>
  );
};

export default AppHeader;
