import React, { useState } from 'react';
import { NavLink } from 'react-router-dom';
import { useSelector } from 'react-redux';
import {
  CContainer,
  CHeader,
  CHeaderBrand,
  CHeaderNav,
  CHeaderToggler,
  CNavLink,
  CNavItem,
  CDropdown,
  CDropdownToggle,
  CDropdownMenu,
  CDropdownItem,
} from '@coreui/react';
import CIcon from '@coreui/icons-react';
import { cilMagnifyingGlass } from '@coreui/icons';
import { AppBreadcrumb } from './index';
import { AppHeaderDropdown } from './header/index';
import logo from '../assets/bee/bee1.png';

const AppHeader = () => {
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const sidebarShow = useSelector((state) => state.sidebarShow);

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  return (
    <CHeader position="sticky" className="mb-5">
      <CContainer fluid>
        <CHeaderBrand className="d-md-none">
          <img src={logo} alt="Logo" width="150" />
        </CHeaderBrand>
        <CHeaderToggler className="ms-auto d-md-none" onClick={toggleMobileMenu}>
          <CIcon icon={cilMagnifyingGlass} />
        </CHeaderToggler>
        <CHeaderNav className={`ms-auto ${isMobileMenuOpen ? 'd-md-none' : 'd-none d-md-flex'}`}>
          <CNavItem>
            <CNavLink to="/" component={NavLink}>
              <img src={logo} alt="Logo" width="150" />
            </CNavLink>
          </CNavItem>
          <CNavItem className="mx-3">
            <CNavLink to="/tweetstats_worldwide" component={NavLink} style={{ fontSize: '18px', color: 'black' }}>
              TweetStats Worldwide
            </CNavLink>
          </CNavItem>
          <CNavItem className="mx-3">
            <CNavLink to="/perspectives_updates" component={NavLink} style={{ fontSize: '18px', color: 'black' }}>
              Perspectives and Updates
            </CNavLink>
          </CNavItem>
          {/* Dropdown Menu */}
          <CNavItem className="mx-3">
            <CDropdown>
              <CDropdownToggle color="secondary">
                Dropdown
              </CDropdownToggle>
              <CDropdownMenu>
                <CDropdownItem>Action</CDropdownItem>
                <CDropdownItem>Another action</CDropdownItem>
                <CDropdownItem>Something else here</CDropdownItem>
              </CDropdownMenu>
            </CDropdown>
          </CNavItem>
          {/* End of Dropdown Menu */}
        </CHeaderNav>
      </CContainer>
    </CHeader>
  );
};

export default AppHeader;
