import React, { useEffect, useState } from "react";

export default function AllTopics() {
  const [records, setRecords] = useState([]);

  useEffect(() => {
    async function fetchRecords() {
      try {
        const response = await fetch("http://localhost:5050/statistics_general/");
        if (!response.ok) {
          throw new Error(`An error occurred: ${response.statusText}`);
        }
        const recordsData = await response.json();
        setRecords(recordsData);
      } catch (error) {
        window.alert(error.message);
      }
    }

    fetchRecords();
  }, []);

  const redirectToTopicPage = (topic) => {
    const topicPageURL = `http:/#/topics/${topic}`;
    window.open(topicPageURL, "_blank");
  };

  const firstHalf = records.slice(0, Math.ceil(records.length / 2));
  const secondHalf = records.slice(Math.ceil(records.length / 2));

  return (
    <div className="home-table1" style={{ display: 'flex', alignItems: 'center', width: '100%', minWidth: '1280px', maxWidth: '1280px' }}>
      <table>
        <tbody>
          {firstHalf.map((record) => (
            <React.Fragment key={record._id}>
              <tr>
                <td style={{ padding: '3px', paddingLeft: '10px' }}></td>
              </tr>
              {Object.entries(record.topics).map(([topic, value]) => (
                <tr key={topic}>
                  <td style={{ padding: '5px', paddingLeft: '10px', width: '50%' }}>
                    <a href="#" onClick={() => redirectToTopicPage(topic)}>
                      {topic}
                    </a>
                  </td>
                  <td className="qtt-background" style={{ padding: '3px' }}>
                    {value}
                  </td>
                </tr>
              ))}
              <tr>
                <td style={{ padding: '3px', paddingLeft: '20px' }}></td>
              </tr>
            </React.Fragment>
          ))}
        </tbody>
      </table>
      <table>
        <tbody>
          {secondHalf.map((record) => (
            <React.Fragment key={record._id}>
              <tr>
                <td style={{ padding: '3px', paddingLeft: '10px' }}></td>
              </tr>
              {Object.entries(record.topics).map(([topic, value]) => (
                <tr key={topic}>
                  <td style={{ padding: '5px', paddingLeft: '10px', width: '50%' }}>
                    <a href="#" onClick={() => redirectToTopicPage(topic)}>
                      {topic}
                    </a>
                  </td>
                  <td className="qtt-background" style={{ padding: '3px' }}>
                    {value}
                  </td>
                </tr>
              ))}
              <tr>
                <td style={{ padding: '3px', paddingLeft: '20px' }}></td>
              </tr>
            </React.Fragment>
          ))}
        </tbody>
      </table>
    </div>
  );
}
