import React, { useEffect, useState } from "react";
import { getLinkPreview, getPreviewFromContent } from "link-preview-js";

export default function Hashtags() {
  const [records, setRecords] = useState([]);

  useEffect(() => {
    async function getRecords() {
      try {
        const response = await fetch("http://localhost:5050/links/");
        if (!response.ok) {
          throw new Error(`An error occurred: ${response.statusText}`);
        }
        const records = await response.json();
        setRecords(records);
      } catch (error) {
        window.alert(error.message);
      }
    }

    getRecords();
  }, []);

  const getFirstTenRecords = () => records.slice(0, 10);

  return (
    <div>
      {getFirstTenRecords().map(async (record) => {
        const linkPreview = await getLinkPreview(record.url);
        return (
          <div key={record._id}>
            <h3>Title: {record.title}</h3>
            <p>Topic: {record.main_topic}</p>
            <p>Description: {record.description}</p>
            <p>
              Link Preview:
              <pre>{JSON.stringify(linkPreview, null, 2)}</pre>
            </p>
          </div>
        );
      })}
    </div>
  );
}
