import React, { useEffect, useState } from "react";
import Container from 'react-bootstrap/Container';

export default function AllTopics() {
  const [records, setRecords] = useState([]);

  useEffect(() => {
    async function fetchRecords() {
      try {
        const response = await fetch("http://localhost:5050/statistics_general/");
        if (!response.ok) {
          throw new Error(`An error occurred: ${response.statusText}`);
        }
        const recordsData = await response.json();
        setRecords(recordsData);
      } catch (error) {
        window.alert(error.message);
      }
    }

    fetchRecords();
  }, []);

  const redirectToTopicPage = (topic) => {
    const topicPageURL = `http:/#/topics/${topic}`;
    window.open(topicPageURL, "_blank");
  };

  const firstHalf = records.slice(0, Math.ceil(records.length / 2));
  const secondHalf = records.slice(Math.ceil(records.length / 2));

  const renderTopicRows = (topicData) =>
    topicData.map((record) =>
      Object.entries(record.topics).map(([topic, value]) => (
        <tr key={topic}>
          <td style={{ padding: '5px', paddingLeft: '10px' }}>
            <a href="#" onClick={() => redirectToTopicPage(topic)}>
              {topic}
            </a>
          </td>
          <td className="qtt-background" style={{ padding: '3px' }}>
            {value}
          </td>
        </tr>
      ))
    );

    return (
        <Container>
          <div className="container text">
            <div className="row align-items-start" style={{display: 'flex', width:'100%', }}>
            <table className="home-table" style={{ width:'100%', minWidth: '1280px', maxWidth:'1280px'}}>
            <div className="col-lg-4 col-md-6 col-sm-12">
            <div className="flex-container" >
                <div className="flex-item">
                
                  <p>Hello</p>
                
                <br></br><br></br><br></br>
                <table className="home-table" style={{ width:'100%', minWidth: '390px', maxWidth:'390px' }}>
                  <thead>
                    <tr>
                    <th colSpan="2" style={{ textAlign: 'center', position: 'relative' }}>
                      <span style={{ position: 'relative', top: '3.6px' }}># Hashtag Trends</span>
                      </th>
                    </tr>
                  </thead>
                  <p>Hello</p>
                </table>
          </div>
          </div>
          </div>
          <div className="col-lg-8 col-md-6 col-sm-12">
                <div className="flex-container" >
                  <div className="flex-item">
                  <div className="row align-items-start" style={{ display: 'flex', width:'100%', minWidth: '840px', maxWidth:'840px'}}>
                  <div className="col-lg-9 col-md-9 col-sm-15" >
                  <p>Hello</p>
                  </div>
                  <div className="col-lg-2 col-md-2 col-sm-8">
                  <p>Hello</p>
                  </div>
                  </div>
                    <br /><br />
                    <h2 style={{ fontFamily: 'Cambria' }}><b>Tweets per Country</b></h2>
                  </div>
                  <div className="flex-item">
                  <p>Hello</p>
                  </div>
                </div>
              </div>
              </table>
            </div>
          </div>
        </Container>
  );
      
    }
