import React, { useEffect, useState } from "react";
import Container from 'react-bootstrap/Container';

export default function AllTopics() {
  const [records, setRecords] = useState([]);

  useEffect(() => {
    async function fetchRecords() {
      try {
        const response = await fetch("http://localhost:5050/statistics_general/");
        if (!response.ok) {
          throw new Error(`An error occurred: ${response.statusText}`);
        }
        const recordsData = await response.json();
        setRecords(recordsData);
      } catch (error) {
        window.alert(error.message);
      }
    }

    fetchRecords();
  }, []);

  const getFirstHalfRecords = () => records.slice(0, records.length / 2);
  const getFirstHalfTopicsEntries = (record) =>
    Object.entries(record.topics).slice(0, records.length / 2);

    const getSecHalfRecords = () => records.slice(records.length / 2);
    const getSecHalfTopicsEntries = (record) =>
      Object.entries(record.topics).slice(records.length / 2);

  const redirectToTopicPage = (topic) => {
    // Modify the URL to the desired topic page
    const topicPageURL = `http:/#/topics/${topic}`;
    window.open(topicPageURL, "_blank");
  };

    return (
        <Container>
          <div className="container text">
            <table className="home-table" style={{ width:'100%', minWidth: '1280px', maxWidth:'1280px'}}>
            <div className="row align-items-start" style={{display: 'flex', width:'100%', }}>
            <div className="col-lg-6 col-md-6 col-sm-6">
            <div className="flex-container" >
                <div className="flex-item">
                
                {getFirstHalfRecords().map((record) => (
        <React.Fragment key={record._id}>
          <tr>
            <td style={{ padding: '3px', paddingInlineStart: '10px' }}>
              
            </td>
          </tr>
          {getFirstHalfTopicsEntries(record).map(([topic, value]) => (
            <tr key={topic}>
              <td style={{ padding: '5px', paddingInlineStart: '10px', width:'82.5%' }}>
                <a href="#" onClick={() => redirectToTopicPage(topic)}>
                  {topic}
                </a>
              </td>
              <td className="qtt-background" style={{ padding: '3px' }}>
                {value}
              </td>
            </tr>
          ))}
          <tr>
            <td style={{ padding: '3px', paddingInlineStart: '20px' }}>
            </td>
          </tr>
        </React.Fragment>
      ))}
          </div>
          </div>
          </div>
          <div className="col-lg-6 col-md-6 col-sm-6">
                <div className="flex-container" >
                  <div className="flex-item">
                  {getSecHalfRecords().map((record) => (
        <React.Fragment key={record._id}>
          <tr>
            <td style={{ padding: '3px', paddingInlineStart: '10px' }}>
              
            </td>
          </tr>
          {getSecHalfTopicsEntries(record).map(([topic, value]) => (
            <tr key={topic}>
              <td style={{ padding: '5px', paddingInlineStart: '10px', width:'82.5%' }}>
                <a href="#" onClick={() => redirectToTopicPage(topic)}>
                  {topic}
                </a>
              </td>
              <td className="qtt-background" style={{ padding: '3px' }}>
                {value}
              </td>
            </tr>
          ))}
          <tr>
            <td style={{ padding: '3px', paddingInlineStart: '20px' }}>
            </td>
          </tr>
        </React.Fragment>
      ))}
                  
                  
                  </div>
                </div>
              </div>
              </div>
              </table>

          </div>
        </Container>
  );
      
    }
