import React, { useEffect, useState } from "react";

export default function AllTopics() {
  const [records, setRecords] = useState([]);

  useEffect(() => {
    async function fetchRecords() {
      try {
        const response = await fetch("http://localhost:5050/statistics_general/");
        if (!response.ok) {
          throw new Error(`An error occurred: ${response.statusText}`);
        }
        const records = await response.json();
        setRecords(records);
      } catch (error) {
        window.alert(error.message);
      }
    }

    fetchRecords();
  }, []);

  const getFirstTenRecords = () => records;
  const getFirstTenTopicsEntries = (record) => Object.entries(record.topics);

  const redirectToTopicPage = (topic) => {
    const topicPageURL = `http:/#/topics/${topic}`;
    window.open(topicPageURL, "_blank");
  };

  return (
    <table className="home-table1" style={{ display: 'flex', alignItems: 'center', width: '100%', minWidth: '1280px', maxWidth: '1280px' }}>
  <tbody>
    {getFirstTenRecords().map((record) => (
      <React.Fragment key={record._id}>
        <tr>
          <td style={{ padding: '3px', paddingLeft: '10px' }}>
            {/* Left Column */}
          </td>
          <td style={{ padding: '3px', paddingLeft: '20px' }}>
            {/* Right Column */}
          </td>
        </tr>
        {getFirstTenTopicsEntries(record).map(([topic, value], index) => (
          <tr key={topic}>
            <td style={{ padding: '5px', paddingLeft: '10px', width: '50%' }}>
              <a href="#" onClick={() => redirectToTopicPage(topic)}>
                {index % 2 === 0 && topic}
              </a>
            </td>
            <td className="qtt-background" style={{ padding: '3px', width: '50%' }}>
              {index % 2 !== 0 && value}
            </td>
          </tr>
        ))}
      </React.Fragment>
    ))}
  </tbody>
</table>

  );
}
