import React, { useEffect, useState } from "react";;

const Record = (props) => {
  const topicsArray = Object.keys(props.record.topics);
  const halfLength = Math.ceil(topicsArray.length / 2);
  const firstHalf = topicsArray.slice(0, halfLength);
  const secondHalf = topicsArray.slice(halfLength);

  const redirectToTopicPage = (topic) => {
    // Modify the URL to the desired topic page
    const topicPageURL = `http:/#/topics/${topic}`;
    window.open(topicPageURL, "_blank");
  };

  return (
    <table className="home-table1" style={{ display: 'flex', alignItems: 'center', width:'100%', minWidth: '1280px', maxWidth:'1280px' }}>
    <div className="row align-items-start" style={{ display: 'flex', alignItems: 'center', width: '100%', minWidth: '1280px', maxWidth: '1280px' }}>
      <div className="col-lg-6 col-md-6 col-sm-12">
        <table>
          <tbody>
            {firstHalf.map((topic, index) => (
              <tr key={topic}>
                <td style={{ padding: '5px', paddingInlineStart: '10px' }}>
                  <a href="#" onClick={() => redirectToTopicPage(topic)}>
                    {topic}
                  </a>
                </td>
                <td className="qtt-background" style={{ padding: '3px' }}>
                  {props.record.topics[topic]}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      <div className="col-lg-6 col-md-6 col-sm-12">
        <table>
          <tbody>
            {secondHalf.map((topic, index) => (
              <tr key={topic}>
                <td style={{ padding: '5px', paddingInlineStart: '10px', width: '82.5%' }}>
                  <a href="#" onClick={() => redirectToTopicPage(topic)}>
                    {topic}
                  </a>
                </td>
                <td className="qtt-background" style={{ padding: '3px' }}>
                  {props.record.topics[topic]}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
    </table>
  );
};



export default function Sentiment() {
  const [records, setRecords] = useState([]);

  useEffect(() => {
    async function getRecords() {
      try {
        const response = await fetch("http://localhost:5050/statistics_general/");

        if (!response.ok) {
          throw new Error(`An error occurred: ${response.statusText}`);
        }

        const records = await response.json();
        setRecords(records);
      } catch (error) {
        window.alert(error.message);
      }
    }

    getRecords();
  }, []);

  // This method will map out the records
  function recordList() {
    return records.map((record) => {
      return <Record record={record} key={record._id} />;
    });
  }
  
  // This following section will display the records and the chart
  return (
    <div>
    <div style={{ display: 'flex' }}>
      <div>
        {recordList()}
      </div> 
      </div>
    </div>
  );
}
