import React, { useEffect, useState } from "react";
import { ReactTinyLink } from "react-tiny-link";
import image from "../../assets/bee/bee.png";

export default function Hashtags() {
  const [records, setRecords] = useState([]);

  useEffect(() => {
    async function getRecords() {
      try {
        const response = await fetch("http://localhost:5050/links/");
        if (!response.ok) {
          throw new Error(`An error occurred: ${response.statusText}`);
        }
        const records = await response.json();
        setRecords(records);
      } catch (error) {
        console.error("Error fetching data:", error);
        window.alert(error.message);
      }
    }
    getRecords();
  }, []);

  const validateUrl = (url) => {
    try {
      new URL(url); // Validate the URL format
      return true;
    } catch (error) {
      console.error("Invalid URL:", url);
      return false;
    }
  };

  return (
    <div style={{ display: "flex" }}>
      <div style={{ flex: 1 }}>
        {records.slice(0, records.length / 2).map((record) => (
          <div key={record._id}>
            {validateUrl(record.url) ? (
              <ReactTinyLink
              cardSize="large"
              showGraphic={!!(record.image || record.defaultMedia)} // Set to true if either image or defaultMedia is present
              maxLine={2}
              minLine={1}
              defaultMedia={image} // Updated usage of defaultMedia
              noCache={true}
              url={record.url}
              loadSecureUrl={true}
            />
            ) : (
              <p>Invalid URL: {record.url}</p>
            )}
            <br />
          </div>
        ))}
      </div>
      <div style={{ flex: 1 }}>
        {records.slice(records.length / 2).map((record) => (
          <div key={record._id}>
            {validateUrl(record.url) ? (
              <ReactTinyLink
                cardSize="large"
                showGraphic={!!(record.image || record.defaultMedia)} // Set to true if either image or defaultMedia is present
                maxLine={2}
                minLine={1}
                defaultMedia={image} // Updated usage of defaultMedia
                noCache={true}
                url={record.url}
                loadSecureUrl={true}
              />
            ) : (
              <p>Invalid URL: {record.url}</p>
            )}
            <br />
          </div>
        ))}
      </div>
    </div>
  );
}
