import React, { useEffect, useState } from "react";
import { ReactTinyLink } from "react-tiny-link";
import axios from "axios";

export default function Hashtags() {
  const [records, setRecords] = useState([]);

  useEffect(() => {
    async function checkUrlWithDelay(url) {
      try {
        const response = await axios.get(url);
        return response.status === 200; // Check if the status code is 200 OK
      } catch (error) {
        return false; // URL is not accessible or encountered an error
      }
    }

    async function getRecords() {
      try {
        const response = await fetch("http://localhost:5050/links/");
        if (!response.ok) {
          throw new Error(`An error occurred: ${response.statusText}`);
        }
        const records = await response.json();
        
        // Process records with a delay between requests
        const accessibleRecords = [];
        for (const record of records) {
          await new Promise(resolve => setTimeout(resolve, 1000)); // Add a delay of 1 second

          const isUrlAccessible = await checkUrlWithDelay(record.url);
          if (isUrlAccessible) {
            accessibleRecords.push(record);
          }
        }

        setRecords(accessibleRecords);
      } catch (error) {
        console.error("Error fetching data:", error);
        window.alert(error.message);
      }
    }

    getRecords();
  }, []);

  return (
    <div style={{ display: "flex" }}>
      <div style={{ flex: 1 }}>
        {records.slice(0, records.length / 2).map((record) => (
          <div key={record._id}>
            <ReactTinyLink
              cardSize="large"
              showGraphic={true}
              maxLine={2}
              noCache={true}
              minLine={1}
              url={record.url}
            />
            <br />
          </div>
        ))}
      </div>
      <div style={{ flex: 1 }}>
        {records.slice(records.length / 2).map((record) => (
          <div key={record._id}>
            <ReactTinyLink
              cardSize="large"
              showGraphic={true}
              maxLine={2}
              minLine={1}
              noCache={true}
              url={record.url}
            />
            <br />
          </div>
        ))}
      </div>
    </div>
  );
}
