import axios from 'axios';

const baseURL = "https://bee-aware-08ab5a1c994e.herokuapp.com/api"; // Update this with your backend API URL

const instance = axios.create({
  baseURL: baseURL,
  headers: {
    "Content-type": "application/json"
  }
});

export default instance;
