import React, { useEffect, useState } from 'react';
import CanvasJSReact from '@canvasjs/react-charts';
import { useParams } from 'react-router-dom';

var CanvasJS = CanvasJSReact.CanvasJS;
var CanvasJSChart = CanvasJSReact.CanvasJSChart;

const LineChart = ({ record }) => {
    const formatData = (data) => {
        const stats = data.daily_statistics.estatisticas_diarias[record.topic];
        let dataPoints = Object.keys(stats).map(date => {
            return {
                x: new Date(date.split('-').reverse().join('-')),
                y: stats[date].Total,
            };
        });
        return dataPoints;
    };

    const options = {
        theme: "light2",
        animationEnabled: true,
        title: {
            text: `Healthy Habits for ${record.topic}`
        },
        axisX: {
            title: "Dates"
        },
        axisY: {
            title: "Total",
            titleFontColor: "#6D78AD",
            lineColor: "#6D78AD",
            labelFontColor: "#6D78AD",
            tickColor: "#6D78AD"
        },
        toolTip: {
            shared: true
        },
        data: [{
            type: "spline",
            name: "Total",
            showInLegend: true,
            xValueFormatString: "DD MMM YYYY",
            yValueFormatString: "#,##0",
            dataPoints: formatData(record)
        }]
    };

    return (
        <CanvasJSChart options={options} />
    );
};

export default function LineChartsContainer() {
    const { topic } = useParams();
    const [records, setRecords] = useState([]);

    useEffect(() => {
        async function getRecords() {
            try {
                const response = await fetch("http://localhost:5050/general_info/");
                if (!response.ok) {
                    throw new Error(`An error occurred: ${response.statusText}`);
                }
                const records = await response.json();
                const matchingRecords = records.filter((r) => r.topic === topic);
                setRecords(matchingRecords);
            } catch (error) {
                window.alert(error.message);
            }
        }

        getRecords();
    }, [topic]);

    if (!records.length) {
        return <div>No record found</div>;
    }

    return (
        <div>
            {records.map((record) => (
                <LineChart record={record} key={record.topic} />
            ))}
        </div>
    );
}
