import React, { useState } from 'react';
import { NavLink } from 'react-router-dom';
import { CContainer, CDropdown, CDropdownToggle, CDropdownMenu, CDropdownItem } from '@coreui/react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTimes, faFacebookF, faGooglePlus, faTwitter, faInstagram, faPinterestP } from '@fortawesome/free-solid-svg-icons';
import logo from '../assets/bee/bee1.png';

const AppHeader = () => {
  const [dropdownOpen, setDropdownOpen] = useState(false);

  const toggleDropdown = () => {
    setDropdownOpen(!dropdownOpen);
  };

  return (
    <div className="side-menu__block">
      <a href="#" className="side-menu__toggler side-menu__close-btn" onClick={toggleDropdown}>
        <FontAwesomeIcon icon={faTimes} />
      </a>
      <div className="side-menu__block-inner">
        <a href="index.html" className="side-menu__logo"><img src={logo} alt="" width="190" /></a>
        <CDropdown isOpen={dropdownOpen} toggle={toggleDropdown}>
          <CDropdownToggle caret>
            Mobile Nav
          </CDropdownToggle>
          <CDropdownMenu>
            <CDropdownItem>
              <NavLink to="/tweetstats_worldwide">TweetStats Worldwide</NavLink>
            </CDropdownItem>
            <CDropdownItem>
              <NavLink to="/perspectives_updates">Perspectives and Updates</NavLink>
            </CDropdownItem>
            {/* Add more dropdown items as needed */}
          </CDropdownMenu>
        </CDropdown>
        <p className="side-menu__block__copy">(c) 2021 <a href="#">Oberlin</a> - All rights reserved.</p>
        <div className="side-menu__social">
          <a href="#"><FontAwesomeIcon icon={faFacebookF} /></a>
          <a href="#"><FontAwesomeIcon icon={faGooglePlus} /></a>
          <a href="#"><FontAwesomeIcon icon={faTwitter} /></a>
          <a href="#"><FontAwesomeIcon icon={faInstagram} /></a>
          <a href="#"><FontAwesomeIcon icon={faPinterestP} /></a>
        </div>
      </div>
    </div>
  );
};

export default AppHeader;
