import React, { useEffect, useState } from "react";
import { BarChart, Bar, XAxis, YAxis, Legend, ResponsiveContainer, LabelList, Label, PieChart, Pie, Cell, Tooltip } from "recharts";

const Record = (props) => (
  <div style={{ display: 'flex', flexDirection: 'row', flexWrap: 'wrap' }}>
    <div style={{ flex: '0 0 50%', padding: '0 10px' }}>
      <div class="metric-container">
        <div class="metric-data">{props.record.engagement_metrics.total_impressions}</div>
        <div class="metric-box1"><strong>Impressions</strong></div>
      </div>

      <div class="metric-container">
        <div class="metric-data">{props.record.engagement_metrics.total_tweets}</div>
        <div class="metric-box2"><strong>Tweets</strong></div>
      </div>

      <div class="metric-container">
        <div class="metric-data">{props.record.engagement_metrics.total_likes}</div>
        <div class="metric-box3"><strong>Likes</strong></div>
      </div>
    </div>

    <div style={{ flex: '0 0 50%', padding: '0 10px' }}>
      <div class="metric-container">
        <div class="metric-data">{props.record.engagement_metrics.total_retweets}</div>
        <div class="metric-box4"><strong>Retweets</strong></div>
      </div>

      <div class="metric-container">
        <div class="metric-data">{props.record.engagement_metrics.total_quotes}</div>
        <div class="metric-box5"><strong>Quotes</strong></div>
      </div>

      <div class="metric-container">
        <div class="metric-data">{props.record.engagement_metrics.total_replys}</div>
        <div class="metric-box6"><strong>Replys</strong></div>
      </div>
    </div>
  </div>
);


export default function Sentiment() {
  const [records, setRecords] = useState([]);

  useEffect(() => {
    async function getRecords() {
      try {
        const response = await fetch("https://bee-aware-0831ce44e802.herokuapp.com/statistics_general/");

        if (!response.ok) {
          throw new Error(`An error occurred: ${response.statusText}`);
        }

        const records = await response.json();
        setRecords(records);
      } catch (error) {
        window.alert(error.message);
      }
    }

    getRecords();
  }, []);

  // This method will map out the records
  function recordList() {
    return records.map((record) => {
      return <Record record={record} key={record._id} />;
    });
  }

  // This following section will display the records and the chart
  return (
    <div>
    <div style={{ display: 'flex' }}>
      <div>
        {recordList()}
      </div> 
      </div>
    </div>
  );
}
