export default RightCaret;
import dt from 'date-and-time';
import type { DisplayDate, WeekStartDay } from './types';

enum DAYS {
  'Sunday' = 0,
  'Monday' = 1,
  'Tuesday' = 2,
  'Wednesday' = 3,
  'Thursday' = 4,
  'Friday' = 5,
  'Saturday' = 6,
}

export enum MONTHS {
  'January' = 0,
  'February' = 1,
  'March' = 2,
  'April' = 3,
  'May' = 4,
  'June' = 5,
  'July' = 6,
  'August' = 7,
  'September' = 8,
  'October' = 9,
  'November' = 10,
  'December' = 11,
}

export const getMonthNameFromNumber = (month: number): string => {
  if (month < 0 || month > 11) {
    throw new Error(`Invalid month number: ${month}`);
  }
  return MONTHS[month];
};

export const getMonthNumberFromName = (month: string): number => {
  switch (month) {
    case 'January':
      return 0
    case 'February':
      return 1
    case 'March':
      return 2
    case 'April':
      return 3
    case 'May':
      return 4
    case 'June':
      return 5
    case 'July':
      return 6
    case 'August':
      return 7
    case 'September':
      return 8
    case 'October':
      return 9
    case 'November':
      return 10
    case 'December':
      return 11
    default:
      throw new Error(`Invalid month name: ${month}`)
  }
};


export const getDayFromNumber = (
  day: number,
  weekStartsFrom: WeekStartDay
): string => {
  if (day < 0 || day > 6) {
    throw new Error(`Invalid month number: ${day}`);
  }
  switch (weekStartsFrom) {
    case 'Monday':
      return DAYS[(day + 1) % 7];
    case 'Sunday':
      return DAYS[day];
    default:
      throw new Error(`Invalid week start day: ${weekStartsFrom}`);
  }
};

export const getDaysOfWeek = (weekStartsFrom: WeekStartDay) => {
  if (weekStartsFrom === 'Monday') {
    return ['Mo', 'Tu', 'We', 'Th', 'Fr', 'Sa', 'Su'];
  }
  return ['Su', 'Mo', 'Tu', 'We', 'Th', 'Fr', 'Sa'];
};

const getMidnight = (n: number): number => {
  const d = new Date(n);
  d.setHours(0, 0, 0, 0);
  return d.valueOf();
}

export const getDatesOfMonth = (date: Date, minDateValue: number, maxDateValue: number, weekStartsFrom: WeekStartDay): DisplayDate[] => {
  const currentYear = date.getFullYear();
  const currentMonth = date.getMonth();
  const minDate = getMidnight(minDateValue);
  const maxDate = getMidnight(maxDateValue);

  // generate dates of each week of the month including the residue dates
  // of the last week of previous month and first week of next month
  const firstDayOfMonth = new Date(currentYear, currentMonth, 1);
  const lastDayOfMonth = new Date(currentYear, currentMonth + 1, 0);

  // first day of the pervious month
  // const previousMonth = dt.addMonths(firstDayOfMonth, -1);
  // last day of the pervious month
  const previousMonthLastDay = dt.addDays(firstDayOfMonth, -1);

  const dates: DisplayDate[] = [];

  // the number of the weekday of the first day of the month
  const firstWeekDayOfMonth = firstDayOfMonth.getDay();
  // the number of the weekday of the last day of the month
  const lastWeekDayOfMonth = lastDayOfMonth.getDay();

  if (weekStartsFrom === 'Sunday') {
    // insert the residual dates of previous month's last week
    for (let i = 1; i <= firstWeekDayOfMonth; i++) {
      const d = dt.addDays(previousMonthLastDay, (i - firstWeekDayOfMonth));
      dates.push({
        date: d,
        active: false,
        ms: d.valueOf(),
      });
    }

    const fullYear = date.getFullYear();
    const fullMonth = date.getMonth();
    // insert the dates of the current month
    for (let i = 1; i <= lastDayOfMonth.getDate(); i++) {
      const d = new Date(fullYear, fullMonth, i);
      const dValue = d.valueOf();
      // compare day of month
      dates.push({
        date: d,
        active: dValue >= minDate && dValue <= maxDate,
        ms: dValue,
      });
    }

    let i = lastWeekDayOfMonth + 1;
    let counter = 1;
    // insert the residual dates of the next month
    while (i <= 6) {
      const d = dt.addDays(lastDayOfMonth, counter++);
      dates.push({
        date: d,
        active: false,
        ms: d.valueOf(),
      });
      i++;
    }
  } else {
    // insert the residual dates of previous month's last week
    for (let i = 1; i < firstWeekDayOfMonth; i++) {
      const d= dt.addDays(previousMonthLastDay, (i - firstWeekDayOfMonth + 1));
      dates.push({
        date: d,
        active: false,
        ms: d.valueOf(),
      });
    }

    const fullYear = date.getFullYear();
    const fullMonth = date.getMonth();
    // insert the dates of the current month
    for (let i = 1; i <= lastDayOfMonth.getDate(); i++) {
      const d = new Date(fullYear, fullMonth, i);
      const dValue = d.valueOf();
      // compare day of month
      dates.push({
        date: d,
        active: dValue >= minDate && dValue <= maxDate,
        ms: dValue,
      });
    }

    let i = lastWeekDayOfMonth;
    let counter = 1;
    // insert the residual dates of the next month
    while (i <= 6) {
      const d = dt.addDays(lastDayOfMonth, counter++);
      dates.push({
        date: d,
        active: false,
        ms: d.valueOf(),
      });
      i++;
    }
  }

  return dates;
};
import React, { useCallback, useEffect, useRef } from 'react';

type OptionProps<T> = {
  /**
   * If the option is currently selected.
   */
  selected: boolean;
  /**
   * The label to display for the option.
   */
  label: string;
  /**
   * The value of the option.
   */
  value: T;
  /**
   * A callback triggered when the option is clicked.
   */
  onClick: (v: T) => void;
  /**
   * If the option is disabled.
   */
  disabled: boolean;
};

/**
 * Custom Option component.
 */
function Option<T extends unknown>({
  selected,
  value,
  label,
  onClick,
  disabled,
  ...props
}: OptionProps<T>): JSX.Element {
  const ref = useRef<HTMLButtonElement>(null);

  const handleClick = useCallback(() => {
    if (!disabled) {
      onClick(value);
    }
  }, [onClick, disabled, value]);

  useEffect(() => {
    if (selected) {
      ref.current?.focus();
    }

    return () => ref.current?.blur();
  }, [selected]);

  return (
    <button
      key={label}
      ref={ref}
      type="button"
      className={`sassy--option ${selected ? 'sassy--option__active' : ''} ${
        disabled ? 'sassy--option__disabled' : ''
      }`}
      onClick={handleClick}
      {...props}
    >
      {label}
    </button>
  );
}

export default Option;
import React, {
  HTMLProps,
  PropsWithChildren,
  useCallback,
  useEffect,
  useMemo,
  useRef,
  useState,
} from 'react';
import CustomOption from './option';

import './styles.css';

export type OptionType<T> = {
  value: T;
  label: string;
  disabled: boolean;
};

type CustomSelectProps<T> = {
  /**
   * The value of the select.
   */
  value: T;
  /**
   * A callback triggered whenever the value of the select changes.
   */
  onChange: (value: T) => void;
  /**
   * The options to display in the select.
   *
   * Format - [{value: [value, label], disabled: boolean}, ...]
   */
  options: OptionType<T>[];
  /**
   * If this input element is disabled
   */
  disabled?: boolean;
  /**
   * How to format the display value
   */
  formatValue?: (value: T) => string;
} & Omit<
  PropsWithChildren<HTMLProps<HTMLDivElement>>,
  'onChange' | 'value' | 'disabled'
>;

/**
 * A custom select component.
 *
 */
function CustomSelect<T>({
  value,
  options,
  onChange,
  className,
  disabled,
  formatValue,
}: CustomSelectProps<T>): JSX.Element {
  const ref = useRef<HTMLDivElement>(null);
  const [isOpen, setIsOpen] = useState<boolean>(false);

  const openOptionsDropdown = useCallback(() => {
    if (!disabled) setIsOpen(true);
  }, [setIsOpen, disabled]);

  const closeOptionsDropdown = useCallback(() => {
    setIsOpen(false);
  }, [setIsOpen]);

  const handleOptionSelect = useCallback(
    (v) => {
      onChange(v);
      closeOptionsDropdown();
    },
    [onChange, closeOptionsDropdown]
  );

  const showDropDown = useMemo(() => isOpen && !disabled, [isOpen, disabled]);

  const displayValue = useMemo(() => formatValue?.(value) ?? value, [
    formatValue,
    value,
  ]);

  useEffect(() => {
    const clickListener = (e: MouseEvent) => {
      if (!ref.current?.contains(e.target as Node)) {
        closeOptionsDropdown();
      }
    };

    const focusOutListener = (e: FocusEvent) => {
      if (!ref.current?.contains(e.relatedTarget as Node)) {
        closeOptionsDropdown();
      }
    };

    document.addEventListener('click', clickListener);
    ref.current?.addEventListener('focusout', focusOutListener);

    return () => {
      document.removeEventListener('click', clickListener);
      ref.current?.removeEventListener('focusout', focusOutListener);
    };
  }, [closeOptionsDropdown, ref.current]);

  return (
    <div
      tabIndex={-1}
      className={`sassy--select__container ${className ?? ''}`}
      ref={ref}
    >
      <p
        className={`sassy--select ${disabled ? 'sassy--select__disabled' : ''}`}
        tabIndex={disabled ? -1 : 0}
        onClick={openOptionsDropdown}
        onFocus={openOptionsDropdown}
      >
        {displayValue}
      </p>
      {showDropDown && (
        <div className="sassy--select__dropdown">
          {options.map(({ value: currValue, label, disabled }) => (
            <CustomOption
              key={label}
              label={label}
              selected={value === currValue}
              value={currValue}
              disabled={disabled}
              onClick={handleOptionSelect}
              aria-label={`Select ${label}`}
            />
          ))}
        </div>
      )}
    </div>
  );
}

export default CustomSelect;
import React, { useMemo } from 'react';
import CustomSelect, { OptionType } from '../components/select';

export type MonthPickerProps = {
  value: string;
  onChange: (year: string) => void;
  disabled: boolean;
};

const months = [
  'January',
  'February',
  'March',
  'April',
  'May',
  'June',
  'July',
  'August',
  'September',
  'October',
  'November',
  'December',
];

const MonthPicker: React.FC<MonthPickerProps> = ({
  value,
  onChange,
  disabled,
}) => {
  const options = useMemo(
    () =>
      months.map(
        (m) =>
          ({
            value: m,
            label: m,
            disabled: false,
          } as OptionType<string>)
      ),
    []
  );

  return (
    <CustomSelect
      className="sdp--select__month"
      value={value}
      onChange={onChange}
      options={options}
      disabled={disabled}
    />
  );
};

export default MonthPicker;
