import React, {
  forwardRef,
  HTMLProps,
  PropsWithRef,
  useCallback,
  useMemo,
  useState,
} from 'react';
import dt from 'date-and-time';

import Header from './header';
import DateButton from './date-button';
import {
  getDatesOfMonth,
  getDaysOfWeek,
  getMonthNumberFromName,
} from './methods';
import { WeekStartDay } from './types';

import './styles.css';

export type DatePickerProps = {
  /**
   * This function is called when the selected date is changed.
   */
  onChange: (date: Date) => void;
  /**
   * The selected date.
   */
  value: Date;
  /**
   * The minimum date that can be selected (inclusive).
   * Default is 1st January 1900
   */
  minDate?: Date;
  /**
   * The maximum date that can be selected (inclusive).
   * Default is 100 years from now
   */
  maxDate?: Date;
  /**
   * Week starts from which day
   */
  weekStartsFrom?: WeekStartDay;
  /**
   * If the DatePicker is disabled
   */
  disabled?: boolean;
} & PropsWithRef<
  Omit<
    HTMLProps<HTMLInputElement>,
    'onChange' | 'selected' | 'options' | 'value' | 'type' | 'name' | 'disabled'
  >
>;

/**
 * DatePicker component to pick dates
 */
const DatePicker = forwardRef<HTMLDivElement, DatePickerProps>(
  (
    {
      onChange,
      value = new Date(),
      minDate,
      maxDate,
      weekStartsFrom = 'Sunday',
      className,
      disabled = false,
      ...props
    },
    ref
  ) => {
    const minDateValue = useMemo(
      () => minDate?.valueOf() ?? new Date(1900, 0, 1).valueOf(),
      [minDate]
    );
    const maxDateValue = useMemo(
      () => maxDate?.valueOf() ?? dt.addYears(new Date(), 100).valueOf(),
      [maxDate]
    );

    // current month and year the user is viewing
    const [openedDate, setOpenedDate] = useState<Date>(value);

    const nextMonth = useCallback(
      () => setOpenedDate((d) => dt.addMonths(d, 1)),
      [setOpenedDate]
    );

    const prevMonth = useCallback(
      () => setOpenedDate((d) => dt.addMonths(d, -1)),
      [setOpenedDate]
    );

    const onMonthChange = useCallback(
      (month: string) => {
        setOpenedDate(
          (d) =>
            new Date(
              d.getFullYear(),
              getMonthNumberFromName(month),
              d.getDate()
            )
        );
      },
      [setOpenedDate]
    );

    const onYearChange = useCallback(
      (year: number) => {
        setOpenedDate((d) => new Date(year, d.getMonth(), d.getDate()));
      },
      [setOpenedDate]
    );

    const handleClick = useCallback((d: Date) => onChange(d), [onChange]);

    const daysOfWeekElements = useMemo(
      () =>
        getDaysOfWeek(weekStartsFrom).map((v) => (
          <p
            key={v}
            className={`sdp--text ${disabled ? 'sdp--text__inactive' : ''}`}
          >
            {v}
          </p>
        )),
      [weekStartsFrom, disabled]
    );

    const daysOfMonthList = useMemo(
      () =>
        getDatesOfMonth(openedDate, minDateValue, maxDateValue, weekStartsFrom),
      [openedDate, minDateValue, maxDateValue, weekStartsFrom]
    );

    return (
      <div
        className={`sdp ${className ?? ''} ${disabled ? 'sdp--disabled' : ''}`}
        aria-label="Date Picker"
        tabIndex={disabled ? -1 : 0}
        ref={ref}
        {...props}
      >
        <Header
          month={openedDate.getMonth()}
          year={openedDate.getFullYear()}
          minDateValue={minDateValue}
          maxDateValue={maxDateValue}
          nextMonth={nextMonth}
          prevMonth={prevMonth}
          onYearChange={onYearChange}
          onMonthChange={onMonthChange}
          disabled={disabled}
        />
        <div className="sdp--grid">{daysOfWeekElements}</div>
        <div className="sdp--grid">
          {daysOfMonthList.map(({ date, active, ms }) => (
            <DateButton
              key={ms}
              date={date}
              active={active && !disabled}
              selected={dt.isSameDay(value, date)}
              onClick={handleClick}
            />
          ))}
        </div>
      </div>
    );
  }
);

DatePicker.displayName = 'DatePicker';

export default DatePicker;
import { Time } from './time-picker/types';

export const getCurrentTime = (): Time => convertDateToTime(new Date());

export const convertDateToTime = (d: Date): Time => ({
  hours: d.getHours(),
  minutes: d.getMinutes(),
});

export const convertTimeToDate = (t: Time, d?: Date): Date => {
  const newDate = d ? new Date(d.valueOf()) : new Date();
  newDate.setHours(t.hours);
  newDate.setMinutes(t.minutes);
  return newDate;
};
/**
 * Time type
 */
export type Time = {
  hours: number;
  minutes: number;
};

/**
 * Time display type
 */
export type TimeDisplay = {
  hours: number;
  minutes: number;
  meridiem?: Meridiem;
};

export type DisplayFormat = '12hr' | '24hr';

export enum Meridiem {
  AM = 'AM',
  PM = 'PM',
}
import { OptionType } from './../components/select';
import { Time, Meridiem, TimeDisplay, DisplayFormat } from './types';

const option = (n: number) => ({
  value: n,
  label: n.toString().padStart(2, '0'),
  disabled: false,
});

/**
 * Creates a time value aligned with the minutes interval from am raw time input
 *
 * @param {Time} time The time value
 * @param {number} interval The interval between each minute select option
 * @returns {Time} Time value rounded to the nearest interval
 */
export const alignTime = (
  { hours, minutes }: Time,
  interval: number,
  lower: boolean = true
): Time => {
  const rem = minutes % interval;
  // round minutes to nearest interval
  if (rem !== 0) {
    minutes = lower ? minutes - rem : minutes + interval - rem;
  }
  return {
    hours,
    minutes,
  };
};

/**
 * Convert Time to a 12hr TimeDisplay format
 *
 * @param selectedTime
 */
export const timeToTimeDisplay = (selectedTime: Time): TimeDisplay => {
  let hours;
  let meridiem = Meridiem.AM;
  if (selectedTime.hours === 0) {
    hours = 12;
  } else if (selectedTime.hours === 12) {
    hours = 12;
    meridiem = Meridiem.PM;
  } else if (selectedTime.hours > 12) {
    hours = selectedTime.hours - 12;
    meridiem = Meridiem.PM;
  } else {
    hours = selectedTime.hours;
  }

  return {
    hours,
    minutes: selectedTime.minutes,
    meridiem,
  };
};

export const generateHourOptions = (
  timeFormat: DisplayFormat
): OptionType<number>[] => {
  if (timeFormat === '12hr') {
    const listOfOptions: OptionType<number>[] = new Array(12);
    listOfOptions[0] = option(12);
    for (let i = 1; i <= 11; i += 1) {
      listOfOptions[i] = option(i);
    }
    return listOfOptions;
  }
  const listOfOptions: OptionType<number>[] = new Array(24);
  for (let i = 0; i <= 23; i += 1) {
    listOfOptions[i] = option(i);
  }
  return listOfOptions;
};

export const generateMinuteOptions = (
  minutesInterval: number
): OptionType<number>[] => {
  let options: OptionType<number>[] = [];
  for (let i = 0; i < 60; i += minutesInterval) {
    options.push(option(i));
  }
  return options;
};

/**
 * Convert a value from 12 hour time format to 24 hour format
 *
 * @param hour hour to convert
 * @param meridiem am or pm
 */
export const convertHourFrom12HrTo24Hr = (
  hour: number,
  meridiem: Meridiem
): number => {
  if (hour === 12) {
    return meridiem === Meridiem.AM ? 0 : 12;
  } else if (meridiem === Meridiem.PM) {
    return hour + 12;
  } else {
    return hour;
  }
};
