import React, { useState, useEffect } from 'react';
import { NavLink } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import {
  CContainer,
  CHeader,
  CHeaderBrand,
  CHeaderDivider,
  CHeaderNav,
  CHeaderToggler,
  CNavLink,
  CNavItem,
} from '@coreui/react';
import CIcon from '@coreui/icons-react';
import { cilMagnifyingGlass } from '@coreui/icons';

import { AppBreadcrumb } from './index';
import { AppHeaderDropdown } from './header/index';
import logo from '../assets/bee/bee1.png';

const AppHeader = () => {
  const dispatch = useDispatch();
  const sidebarShow = useSelector((state) => state.sidebarShow);
  const [isMobile, setIsMobile] = useState(false);

  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth <= 768); // Change the breakpoint as per your requirement
    };
    handleResize(); // Call the function to set initial state
    window.addEventListener('resize', handleResize); // Add event listener for window resize
    return () => window.removeEventListener('resize', handleResize); // Clean up on unmount
  }, []);

  return (
    <CHeader position="sticky" className="mb-5">
      <CContainer>
        <CHeaderNav className="d-none d-md-flex me-auto align-items-center">
          <CNavItem>
            <CNavLink to="/" component={NavLink}>
              <img src={logo} alt="Logo" width="150" />
            </CNavLink>
          </CNavItem>
          <CNavItem className="mx-3">
            <CNavLink to="/tweetstats_worldwide" component={NavLink} style={{ fontSize: '18px', color: 'black' }} size="lg">
              TweetStats Worldwide
            </CNavLink>
          </CNavItem>
          <CNavItem className="mx-3">
            <CNavLink to="/perspectives_updates" component={NavLink} style={{ fontSize: '18px', color: 'black' }} size="lg">
              Perspectives and Updates
            </CNavLink>
          </CNavItem>
          <AppHeaderDropdown className="mx-auto d-md-none"/>
        </CHeaderNav>
      </CContainer>
    </CHeader>
  );
};

export default AppHeader;
