import React, { useEffect, useState } from "react";
import { FaAngleRight } from 'react-icons/fa';


const Record = (props) => {
  const topicsArray = Object.keys(props.record.topics);
  const halfLength = Math.ceil(topicsArray.length / 2);
  const firstHalf = topicsArray.slice(0, halfLength);
  const secondHalf = topicsArray.slice(halfLength);

  const redirectToTopicPage = (topic) => {
    // Modify the URL to the desired topic page
    const topicPageURL = `http:/#/topics/${topic}`;
    window.open(topicPageURL, "_blank");
  };

  return (

    <table className="home-table1" style={{ display: 'flex', alignItems: 'center', width:'100%', minWidth: '1280px', maxWidth:'1280px' }}>
      <div className="col-lg-12 col-md-12 col-sm-12">
        <table>
          <tbody>
          {props.filteredTopics.map((topic, index) => (
            <table key={topic} className="home-table2" style={{ marginTop:'20px', marginBottom:'20px',marginLeft:'40px', alignItems: 'center', width: '100%', minWidth: '580px', maxWidth: '580px' }}>
              <thead>
                <tr>
                  <th style={{ display: 'flex', padding:'12px' }}>
                    <span style={{ marginRight: 'auto' }}>{topic}</span>
                    <a href="#" onClick={() => redirectToTopicPage(topic)}>
                      <FaAngleRight />
                    </a>
                  </th>
                </tr>
              </thead>
              <tbody>
                <tr>
                  <td style={{ display: 'flex', padding:'12px' }}>
                  Total Tweets: {props.record.topics[topic]}
                  </td>
                </tr>
              </tbody>
            </table>
          ))}
          </tbody>
        </table>
      </div>
    </table>
  );
};



export default function Sentiment() {
  const [records, setRecords] = useState([]);
  const [selectedLetter, setSelectedLetter] = useState('');

  useEffect(() => {
    async function getRecords() {
      try {
        const response = await fetch("http://localhost:5050/statistics_general/");

        if (!response.ok) {
          throw new Error(`An error occurred: ${response.statusText}`);
        }

        const records = await response.json();
        setRecords(records);
      } catch (error) {
        window.alert(error.message);
      }
    }

    getRecords();
  }, []);

  function recordList() {
    return records.map((record) => {
      const topicsArray = Object.keys(record.topics);
      const filteredTopics = selectedLetter
        ? topicsArray.filter((topic) => topic.startsWith(selectedLetter))
        : topicsArray;
  
      return (
        <Record
          record={record}
          key={record._id}
          filteredTopics={filteredTopics}
        />
      );
    });
  }
  
  
  // This following section will display the records and the chart
  return (
    <div>
    <div style={{ display: 'flex' }}>
    <select
      value={selectedLetter}
      onChange={(e) => setSelectedLetter(e.target.value)}>
      <option value="">All</option>
      {Array.from({ length: 26 }, (_, index) => (
        <option key={index} value={String.fromCharCode(65 + index)}>
          {String.fromCharCode(65 + index)}
        </option>
      ))}
    </select>
      <div>
        {recordList()}
      </div> 
      </div>
    </div>
  );
}
