import React, { useEffect, useState } from "react";
import { ReactTinyLink } from "react-tiny-link";

export default function Hashtags() {
  const [records, setRecords] = useState([]);
  const corsProxy = "https://cors-anywhere.herokuapp.com/";
  const apiUrl = "http://localhost:5050/links/"; // Replace with your API URL

  useEffect(() => {
    async function getRecords() {
      try {
        const response = await fetch(corsProxy + apiUrl);
        if (!response.ok) {
          throw new Error(`An error occurred: ${response.statusText}`);
        }
        const records = await response.json();
        setRecords(records);
      } catch (error) {
        console.error("Error fetching data:", error);
        window.alert(error.message);
      }
    }
    getRecords();
  }, []);

  return (
    <div style={{ display: "flex" }}>
      <div style={{ flex: 1 }}>
        {records.slice(0, records.length / 2).map((record) => (
          <div key={record._id}>
            <ReactTinyLink
              cardSize="large"
              showGraphic={true} // Set showGraphic to true if image exists, false otherwise
              maxLine={2}
              noCache={true}
              minLine={1}
              url={record.url}
            />
            <br />
          </div>
        ))}
      </div>
      <div style={{ flex: 1 }}>
        {records.slice(records.length / 2).map((record) => (
          <div key={record._id}>
            <ReactTinyLink
              cardSize="large"
              showGraphic={true} // Set showGraphic to true if image exists, false otherwise
              maxLine={2}
              minLine={1}
              noCache={true}
              url={record.url}
            />
            <br />
          </div>
        ))}
      </div>
    </div>
  );
}
