import React, { useEffect, useState } from "react";
import { MapContainer, TileLayer, CircleMarker, Popup, useMap, ZoomControl } from "react-leaflet";
import L from "leaflet";
import "leaflet/dist/leaflet.css";

const Record = (props) => {
  const heatmapData = Object.entries(props.record.geographical).map(([country, value]) => ({
    country: country,
    value: value,
    coordinates: [Math.random() * 180 - 90, Math.random() * 360 - 180], // Replace with actual coordinates if available
  }));

  return (
    <MapContainer style={{ height: "512px", width: "512px" }} center={[0, 0]} zoom={1} doubleClickZoom={false} attributionControl={false} zoomControl={false} maxBounds={[[90, -180], [-90, 180]]}>
      <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" noWrap={true}/>
      {heatmapData.map((data) => (
        <CircleMarker
          key={data.country}
          center={data.coordinates}
          radius= {(data.value) ** (1/4) * 5}
          color="#F9E076"
          fillColor="#F9E076"
          fillOpacity={0.8}
        >
          <Popup>
            <div>
              <strong>Country: </strong>{data.country}
            </div>
            <div>
              <strong>Value: </strong>{data.value}
            </div>
          </Popup>
        </CircleMarker>
      ))}
    </MapContainer>
  );
};

export default function Map() {
  const [records, setRecords] = useState([]);

  useEffect(() => {
    async function getRecords() {
      const response = await fetch(`http://localhost:5050/statistics_general/`);

      if (!response.ok) {
        const message = `An error occurred: ${response.statusText}`;
        window.alert(message);
        return;
      }

      const records = await response.json();
      setRecords(records);
    }

    getRecords();
  }, []);

  return (
    <div>
      {records.map((record) => (
        <Record record={record} key={record._id} />
      ))}
    </div>
  );
}
