import React, { useEffect, useState } from "react";
import Container from 'react-bootstrap/Container';

export default function AllTopics() {
  const [records, setRecords] = useState([]);

  useEffect(() => {
    async function fetchRecords() {
      try {
        const response = await fetch("http://localhost:5050/statistics_general/");
        if (!response.ok) {
          throw new Error(`An error occurred: ${response.statusText}`);
        }
        const recordsData = await response.json();
        setRecords(recordsData);
      } catch (error) {
        window.alert(error.message);
      }
    }

    fetchRecords();
  }, []);

  const redirectToTopicPage = (topic) => {
    // Modify the URL to the desired topic page
    const topicPageURL = `http:/#/topics/${topic}`;
    window.open(topicPageURL, "_blank");
  };

  const renderTopics = (topics) => {
    return Object.entries(topics).map(([topic, value]) => (
      <tr key={topic}>
        <td style={{ padding: '5px', paddingLeft: '10px', width: '82.5%' }}>
          <a href="#" onClick={() => redirectToTopicPage(topic)}>
            {topic}
          </a>
        </td>
        <td className="qtt-background" style={{ padding: '3px' }}>
          {value}
        </td>
      </tr>
    ));
  };

  const halfIndex = Math.ceil(records.length / 2);
  const firstHalf = records.slice(0, halfIndex);
  const secondHalf = records.slice(halfIndex);

  return (
    <Container>
      <div className="container text">
        <table className="home-table" style={{ width: '100%', minWidth: '1280px', maxWidth: '1280px' }}>
          <tr className="row align-items-start" style={{ display: 'flex', width: '100%' }}>
            <td className="col-lg-6 col-md-6 col-sm-6">
              <div className="flex-container">
                {firstHalf.map((record) => (
                  <React.Fragment key={record._id}>
                    <tr>
                      <td style={{ padding: '3px', paddingLeft: '10px' }}></td>
                    </tr>
                    {renderTopics(record.topics)}
                    <tr>
                      <td style={{ padding: '3px', paddingLeft: '20px' }}></td>
                    </tr>
                  </React.Fragment>
                ))}
              </div>
            </td>
            <td className="col-lg-6 col-md-6 col-sm-6">
              <div className="flex-container">
                {secondHalf.map((record) => (
                  <React.Fragment key={record._id}>
                    <tr>
                      <td style={{ padding: '3px', paddingLeft: '10px' }}></td>
                    </tr>
                    {renderTopics(record.topics)}
                    <tr>
                      <td style={{ padding: '3px', paddingLeft: '20px' }}></td>
                    </tr>
                  </React.Fragment>
                ))}
              </div>
            </td>
          </tr>
        </table>
      </div>
    </Container>
  );
}
